import {useState} from 'react';

function App() {
  const [calc, setCalc] = useState("");
  const [result, setResult] = useState("");

  const ops = ['/', '*', '+', '-', '.'];

  const updateCalc = value => {
    setCalc(calc + value);
  }

  const createDigits = () => {
    const digits = [];
    for (let i=1; i<10; i++) {
      digits.push(
        <button key={i}>{i}</button>
      )
    }

    return digits;
  }

  return (
    <div className="App">
      <div className="calculator">
        <div className="display">
          {result } {calc || "0"}
        </div>
        <div className="operators">
          <button>/</button>
          <button>*</button>
          <button>+</button>
          <button>-</button>

          <button>DEL</button>
        </div>

        <div className="digits">
          {createDigits()}
          <button>0</button>
          <button>.</button>
          <button>=</button>
        </div>
      </div>
    </div>
  );
}

export default App;
